package utilitarios;

import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.File;
import java.io.IOException;
import javax.imageio.ImageIO;
import javax.swing.Icon;
import javax.swing.ImageIcon;
import javax.swing.JFileChooser;
import javax.swing.filechooser.FileNameExtensionFilter;
import Decoder.BASE64Decoder;
import Decoder.BASE64Encoder;
import model.bean.Usuario;

public class ImageEncoder {

	//foto gen√©rica em base64
	String fotoGenerica = "";

	
	public String lerImagem() {

		final JFileChooser jfc = new JFileChooser();
		jfc.setDialogTitle("Escolha Uma Imagem: ");
		jfc.setAcceptAllFileFilterUsed(false);
		FileNameExtensionFilter filter = new FileNameExtensionFilter("*.jpg", "jpg");
		jfc.addChoosableFileFilter(filter);

		int returnVal = jfc.showOpenDialog(null);
		if (returnVal == JFileChooser.APPROVE_OPTION) {
			File file = jfc.getSelectedFile();

			try {

				BufferedImage img = ImageIO.read(new File(file.toString()));
				String imagem = encodeToString(img);
				return imagem;
			} catch (IOException e) {

				return "";
			}
		}
		return "";
	}

	public Icon ConverterImagem(Usuario u) throws IOException {
		// converte bufferedImage  para icon
		String foto = u.getFoto();
		BufferedImage img = null;
		ImageIcon icon;
		Icon icone;

		if (!"".equals(foto)) {
			img = decodeToImage(foto);
			icon = new ImageIcon(img);
			icone = new ImageIcon(icon.getImage().getScaledInstance(300, 400, 50));
			return icone;
		} else {
			foto = fotoGenerica;
			img = decodeToImage(foto);
			icon = new ImageIcon(img);
			icone = new ImageIcon(icon.getImage().getScaledInstance(300, 400, 50));
			return icone;
		}

	}

	public static String encodeToString(BufferedImage image) {
	// encodar para string base 64{
		String imageString = null;
		ByteArrayOutputStream bos = new ByteArrayOutputStream();

		try {
			ImageIO.write(image, "jpg", bos);
			byte[] imageBytes = bos.toByteArray();
			BASE64Encoder encoder = new BASE64Encoder();
			imageString = encoder.encode(imageBytes);
			bos.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return imageString;
	}

	public static BufferedImage decodeToImage(String imageString) {
		//Decoda para Imagem
		BufferedImage image = null;
		byte[] imageByte;
		try {
			BASE64Decoder decoder = new BASE64Decoder();
			imageByte = decoder.decodeBuffer(imageString);
			ByteArrayInputStream bis = new ByteArrayInputStream(imageByte);
			image = ImageIO.read(bis);
			bis.close();
		} catch (Exception e) {
			e.printStackTrace();
		}
		return image;
	}

}
